import React, {useContext, useState, useEffect} from 'react'
import ApiContext from '../context/ApiContext'
import {
    Row,
    Col,
    Container,
    Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle
} from 'reactstrap'

function List() {
    const {list} = useContext(ApiContext)
    const [listing, setlisting] = useState([]);
    const [fetchData, setfetchData] = useState(false);
    const [limit, setlimit] = useState(0)

    useEffect(() => {
        if (list) {
            let x = list;
            x = x.slice(0,20)
            setlisting(x)
        }
    }, [list])

    useEffect(() => {
        if (!fetchData) return;
        fetchMoreListItems(20);
    }, [fetchData]);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    function handleScroll() {
        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight || limit){
            setlimit((x) => x + 1)
            setfetchData(true);
        }
    }

    function fetchMoreListItems(q) {
        setTimeout(() => {
            let l = listing.length;
            let x = list;
            let y = q 
            let z = limit * 20
            x = x.slice(z,z + 20)
            setlisting(prevState => ([...prevState, x]));
            setfetchData(false);
        }, 2000);
    }
    return (
        <Row>
        {
        listing.map((v,i) => {
            let size = `${v.size}px`;
            let cents = `$ ${v.price / 100}`
            let finalDate = null
            let oldDate = new Date(v.date);
            let today = new Date()
            let weekAgo = new Date()
            let week = today.getDate() - 7 ;
            weekAgo.setDate(week)
            console.log(weekAgo)
            if (oldDate.getTime() > weekAgo.getTime() && oldDate.getTime() < today.getTime() ) {
                let diff = ( today.getTime() - oldDate.getTime() ) / (1000 * 3600 * 24);
                finalDate = `${diff} days ago`
            }else{
                let day = oldDate.getDate();
                let month = oldDate.getMonth() + 1;
                let year = oldDate.getFullYear();

                finalDate = `${day}/${month}/${year}`
            }

            

            return(
                <Col sm="6" xs="6" md="2" key={i} style={{cursor:'pointer'}}>
                    <Card style={{height:'170.15px',width:'140.2px'}} >
                        <CardBody style={{padding:'0.25rem'}}>
                            <CardTitle style={{fontSize:'30px',lineHeight:'3',textAlign:'center',letterSpacing:'0',color:'#4d4d4d', fontWeight:'bold'}}>{v.face}</CardTitle>
                            <CardText style={{position:"absolute",bottom:'0',color:'#E01A1A',fontSize:'14px', fontWeight:'bold'}}>{size}</CardText>
                            <CardText style={{position:"absolute",bottom:'0',color:'#E01A1A',fontSize:'14px', fontWeight:'bold'}}>{cents}</CardText>
                            <CardText style={{position:"absolute",bottom:'0',color:'#E01A1A',fontSize:'14px', fontWeight:'bold'}}>{finalDate}</CardText>
                        </CardBody>
                    </Card> 
                    <br/>  
                </Col>
            )
        })
        }
        </Row>
    )
}

export default List
